    ELEMENTAL LOGICAL(4) FUNCTION FOR_IEEE_ROUND_EQ (LP, RP)
      !DEC$ ATTRIBUTES DEFAULT :: FOR_IEEE_ROUND_EQ    
      TYPE(IEEE_ROUND_TYPE), INTENT(IN) :: LP, RP
      
