      REAL(8), INTENT(IN) :: Y
      REAL(8)  R1,R2
      INTEGER(8), DIMENSION(1) :: EI1,EI2

      EQUIVALENCE (R1, EI1)
      EQUIVALENCE (R2, EI2)

! Sign position.       

