!PULSEMODULE	
MODULE PEAK_MODULE
CONTAINS
subroutine peakdet(maxtab,mintab,n,v,delta,x)

    use, intrinsic :: ieee_arithmetic
    implicit none

    integer, intent(in)            :: n
    real, intent(in)               :: v(n), delta
    real, intent(in), optional     :: x(n)
    real, intent(out), allocatable :: maxtab(:,:), mintab(:,:)
    integer                        :: nargin, lookformax, i, j, c, d
    real                           :: a, NaN, Pinf, Minf, &
                                      mn, mx, mnpos, mxpos, this, &
                                      x2(n), maxtab_tmp(n,2), mintab_tmp(n,2)

    nargin=command_argument_count()
    
    if (nargin < 6) then
      forall(j=1:n) x2(j)=dble(j)
    else
      x2=x
      if (size(v) /= size(x)) then
        print*,'Input vectors v and x must have same length'
      end if
    end if
    
    if (size((/ delta /)) > 1) then
      print*,'Input argument DELTA must be a scalar'
    end if
    
    if (delta <= 0) then
      print*,'Input argument DELTA must be positive'
    end if
    
    NaN=ieee_value(a, ieee_quiet_nan)
    Pinf=ieee_value(a, ieee_positive_inf)
    Minf=ieee_value(a, ieee_negative_inf)
    
    mn=Pinf
    mx=Minf
    mnpos=NaN
    mxpos=NaN
    lookformax=1
    
    c=0
    d=0
    maxtab_tmp(:,:)=NaN
    mintab_tmp(:,:)=NaN
    
    do i=1,n
      this = v(i)
      if (this > mx) then
        mx = this
        mxpos = x2(i)
      end if
      if (this < mn) then
        mn = this
        mnpos = x2(i)
      end if
      if (lookformax==1) then
        if (this < mx-delta) then
          c=c+1
          maxtab_tmp(c,:)=(/ mxpos, mx /)
          mn = this
          mnpos = x2(i)
          lookformax = 0
        end if
      else
        if (this > mn+delta) then
          d=d+1
          mintab_tmp(d,:)=(/ mnpos, mn /)
          mx = this
          mxpos = x2(i)
          lookformax = 1
        end if
      end if
    end do
    
    allocate(maxtab(c,2))
    allocate(mintab(d,2))
    where (.not.ieee_is_nan(maxtab_tmp))
      maxtab=maxtab_tmp
      mintab=mintab_tmp
    end where

end subroutine peakdet
END
    
       
    SUBROUTINE PULSE_CALC(N_POINTS,MEAN_PULSE,TS,X_ARR_INITIAL,Y_ARR_INITIAL,Z_ARR_INITIAL)
    !DEC$ ATTRIBUTES dllexport, stdcall, reference, alias : 'PULSE_CALC' :: PULSE_CALC
    USE PEAK_MODULE
    IMPLICIT NONE
    CHARACTER*8 APROTO,FTYPE
    INTEGER N_POINTS,MEAN_PULSE,N_ACC,I,J,NSAMPS,IORD,PASSES,NE_MAX
    REAL TRBNDW,A,FLO,FHI,TS,DELTA,PERIOD_MEAN
    REAL X_ARR_INITIAL(1:N_POINTS),Y_ARR_INITIAL(1:N_POINTS),Z_ARR_INITIAL(1:N_POINTS)
    REAL X_MOVING_AVERAGE(1:N_POINTS),X_MA(1:N_POINTS-14),X_MEAN,X_DEV,X_DATA_ARRAY(1:N_POINTS-14)
    REAL Y_MOVING_AVERAGE(1:N_POINTS),Y_MA(1:N_POINTS-14),Y_MEAN,Y_DEV,Y_DATA_ARRAY(1:N_POINTS-14)
    REAL Z_MOVING_AVERAGE(1:N_POINTS),Z_MA(1:N_POINTS-14),Z_MEAN,Z_DEV,Z_DATA_ARRAY(1:N_POINTS-14)
    REAL XYZ_DATA_ARRAY(1:N_POINTS-14)
    
    REAL,ALLOCATABLE :: maxtab(:,:),mintab(:,:)
    
    N_ACC=15
    DO J=N_ACC/2+1,N_POINTS-N_ACC/2
        X_MOVING_AVERAGE(J)=(X_ARR_INITIAL(J)-SUM(X_ARR_INITIAL(J-N_ACC/2:J+N_ACC/2))/N_ACC)
    END DO
    !------------------------------------
    X_MA=X_MOVING_AVERAGE(8:N_POINTS-7)
    X_MEAN=SUM(X_MA)/(N_POINTS-14)
    X_DEV=SQRT(SUM((X_MA-X_MEAN)**2)/(N_POINTS-14-1.0))
    X_MA=(X_MA-X_MEAN)/X_DEV
    !------------------------------------
    X_DATA_ARRAY=X_MA
    NSAMPS=N_POINTS-14
    APROTO='BU'
    TRBNDW=0.0
    A=0.0
    IORD=4
    FTYPE='BP'
    FLO=10.0
    FHI=13.0
    !TS=0.01
    PASSES=2
    CALL XAPIIR(X_DATA_ARRAY,NSAMPS,APROTO,TRBNDW,A,IORD,FTYPE,FLO,FHI,TS,PASSES)
    !------------------------------------
    DO J=N_ACC/2+1,N_POINTS-N_ACC/2
        Y_MOVING_AVERAGE(J)=(Y_ARR_INITIAL(J)-SUM(Y_ARR_INITIAL(J-N_ACC/2:J+N_ACC/2))/N_ACC)
    END DO
    !------------------------------------
    Y_MA=Y_MOVING_AVERAGE(8:N_POINTS-7)
    Y_MEAN=SUM(Y_MA)/(N_POINTS-14)
    Y_DEV=SQRT(SUM((Y_MA-Y_MEAN)**2)/(N_POINTS-14-1.0))
    Y_MA=(Y_MA-Y_MEAN)/Y_DEV
    !------------------------------------
    Y_DATA_ARRAY=Y_MA
    CALL XAPIIR(Y_DATA_ARRAY,NSAMPS,APROTO,TRBNDW,A,IORD,FTYPE,FLO,FHI,TS,PASSES)
    !------------------------------------
    DO J=N_ACC/2+1,N_POINTS-N_ACC/2
        Z_MOVING_AVERAGE(J)=(Z_ARR_INITIAL(J)-SUM(Z_ARR_INITIAL(J-N_ACC/2:J+N_ACC/2))/N_ACC)
    END DO
    !------------------------------------
    Z_MA=Z_MOVING_AVERAGE(8:N_POINTS-7)
    Z_MEAN=SUM(Z_MA)/(N_POINTS-14)
    Z_DEV=SQRT(SUM((Z_MA-Z_MEAN)**2)/(N_POINTS-14-1.0))
    Z_MA=(Z_MA-Z_MEAN)/Z_DEV
    !------------------------------------
    Z_DATA_ARRAY=Z_MA
    CALL XAPIIR(Z_DATA_ARRAY,NSAMPS,APROTO,TRBNDW,A,IORD,FTYPE,FLO,FHI,TS,PASSES)
    !------------------------------------
    XYZ_DATA_ARRAY=SQRT(X_DATA_ARRAY**2+Y_DATA_ARRAY**2+Z_DATA_ARRAY**2)
    !------------------------------------
    NSAMPS=N_POINTS-14
    IORD=2
    FLO=0.66
    FHI=2.5
    CALL XAPIIR(XYZ_DATA_ARRAY,NSAMPS,APROTO,TRBNDW,A,IORD,FTYPE,FLO,FHI,TS,PASSES)
    !------------------------------------
    DELTA=0.04
    CALL PEAKDET(MAXTAB,MINTAB,NSAMPS,XYZ_DATA_ARRAY,DELTA)
    NE_MAX=UBOUND(MAXTAB,1)
    !----------------------------------
    PERIOD_MEAN=0.0
    DO I=1,NE_MAX-1
        PERIOD_MEAN=PERIOD_MEAN+(MAXTAB(I+1,1)-MAXTAB(I,1))
    END DO
    PERIOD_MEAN=PERIOD_MEAN/(NE_MAX-1)
    MEAN_PULSE=60.0/(PERIOD_MEAN*TS)
    END SUBROUTINE
!----------------------------------------------------------------
! XAPIIR -- SUBROUTINE:   IIR FILTER DESIGN AND IMPLEMENTATION                  
!                                                                               
!  ARGUMENTS:                                                                   
!  ----------                                                                   
!                                                                               
!    DATA           REAL ARRAY CONTAINING SEQUENCE TO BE FILTERED               
!                     ORIGINAL DATA DESTROYED, REPLACED BY FILTERED DATA        
!                                                                               
!    NSAMPS         NUMBER OF SAMPLES IN DATA                                   
!                                                                               
!                                                                               
!    APROTO         CHARACTER*8 VARIABLE, CONTAINS TYPE OF ANALOG               
!                     PROTOTYPE FILTER                                          
!                     '(BU)TTER  ' -- BUTTERWORTH FILTER                        
!                     '(BE)SSEL  ' -- BESSEL FILTER                             
!                     'C1      ' -- CHEBYSHEV TYPE I                            
!                     'C2      ' -- CHEBYSHEV TYPE II                           
!                                                                               
!    TRBNDW         TRANSITION BANDWIDTH AS FRACTION OF LOWPASS                 
!                   PROTOTYPE FILTER CUTOFF FREQUENCY.  USED                    
!                   ONLY BY CHEBYSHEV FILTERS.                                  
!                                                                               
!    A              ATTENUATION FACTOR.  EQUALS AMPLITUDE                       
!                   REACHED AT STOPBAND EDGE.  USED ONLY BY                     
!                   CHEBYSHEV FILTERS.                                          
!                                                                               
!    IORD           ORDER (#POLES) OF ANALOG PROTOTYPE                          
!                   NOT TO EXCEED 10 IN THIS CONFIGURATION.  4 - 5              
!                   SHOULD BE AMPLE.                                            
!                                                                               
!    TYPE           CHARACTER*8 VARIABLE CONTAINING FILTER TYPE                 
!                     'LP' -- LOW PASS                                          
!                     'HP' -- HIGH PASS                                         
!                     'BP' -- BAND PASS                                         
!                     'BR' -- BAND REJECT                                       
!                                                                               
!    FLO            LOW FREQUENCY CUTOFF OF FILTER (HERTZ)                      
!                   IGNORED IF TYPE = 'LP'                                      
!                                                                               
!    FHI            HIGH FREQUENCY CUTOFF OF FILTER (HERTZ)                     
!                   IGNORED IF TYPE = 'HP'                                      
!                                                                               
!    TS             SAMPLING INTERVAL (SECONDS)                                 
!                                                                               
!    PASSES           INTEGER VARIABLE CONTAINING THE NUMBER OF PASSES          
!                   1 -- FORWARD FILTERING ONLY                                 
!                   2 -- FORWARD AND REVERSE (I.E. ZERO PHASE) FILTERING        
!                                                                               
!                                                                               
!  SUBPROGRAMS REFERENCED:  BILIN2, BUROOTS, WARP, CUTOFFS, LPTHP, LPTBP,       
!    LP, LPTBR, BEROOTS, C1ROOTS, C2ROOTS, CHEBPARM, DESIGN, APPLY              
!                                                                               
      SUBROUTINE XAPIIR( DATA_ARRAY, NSAMPS, APROTO, TRBNDW, A, IORD, FTYPE, FLO, FHI, TS, PASSES )                   
!                                                                               
      IMPLICIT NONE
!                                                                               
        REAL*4  DATA_ARRAY(1)                                               
        CHARACTER*8  FTYPE, APROTO                                        
        INTEGER  NSAMPS, PASSES, IORD                                 
        REAL*4  TRBNDW, A, FLO, FHI, TS
        
        REAL*4  SN(30), SD(30)                  
        LOGICAL ZP                  
        INTEGER  NSECTS

! CHECK IF FLO IS SMALLER THAN FHI AND BOTH POSITIVE
        IF (FLO .LE. 0 .OR. FHI .LE. 0 .OR. FLO .GE. FHI) STOP 'IN XAPIIR SUBROUTINE, FLO > 0, FHI > 0 AND FLO < FHI IS NOT SATISFIED'


!                                                                               
!  Filter designed                                                              
!                                                                               
        CALL DESIGN( IORD, FTYPE(1:2), APROTO(1:2), A, TRBNDW, FLO, FHI, TS, SN, SD, NSECTS )
!                                                                               
!  Filter data                                                                  
!                                                                               
        IF (   PASSES .EQ. 1 ) THEN                                     
          ZP = .FALSE.                                                  
        ELSE                                                            
          ZP = .TRUE.                                                   
        END IF                                                          
        CALL APPLY( DATA_ARRAY, NSAMPS, ZP, SN, SD, NSECTS )                  
!                                                                               
      RETURN                                                            
      END 
!----------------------------------------------------------------------------
!                                                               APPLY           
!  Subroutine to apply an iir filter to a data sequence.                        
!    The filter is assumed to be stored as second order sections.               
!    Filtering is in-place.                                                     
!    Zero-phase (forward and reverse) is an option.                             
!                                                                               
!  Input Arguments:                                                             
!  ----------------                                                             
!                                                                               
!    DATA                           Array containing data                       
!                                                                               
!    NSAMPS                         Number of data samples                      
!                                                                               
!    ZP                             Logical variable, true for                  
!                                     zero phase filtering, false               
!                                     for single pass filtering                 
!                                                                               
!    SN                             Numerator polynomials for second            
!                                     order sections.                           
!                                                                               
!    SD                             Denominator polynomials for second          
!                                     order sections.                           
!                                                                               
!    NSECTS                         Number of second-order sections             
!                                                                               
!  Output Arguments:                                                            
!  -----------------                                                            
!                                                                               
!    DATA                          Data array (same as input)                   
!                                                                               
!                                                                               
      SUBROUTINE APPLY( DATA_ARRAY, NSAMPS, ZP, SN, SD, NSECTS )              
!                                                                             
      IMPLICIT NONE

      REAL*4 SN(1), SD(1), DATA_ARRAY(1)
      INTEGER NSAMPS,NSECTS
      LOGICAL ZP    
                                      
      INTEGER JPTR,J,I
      REAL*4 X1,X2,Y1,Y2,B0,B1,B2,A1,A2,OUTPUT
                                                          
!                                                                               
        JPTR = 1                                                        
        DO    1 J = 1, NSECTS                                           
!                                                                               
          X1 = 0.0                                                      
          X2 = 0.0                                                      
          Y1 = 0.0                                                      
          Y2 = 0.0                                                      
          B0 = SN(JPTR)                                                 
          B1 = SN(JPTR+1)                                               
          B2 = SN(JPTR+2)                                               
          A1 = SD(JPTR+1)                                               
          A2 = SD(JPTR+2)                                               
!                                                                               
          DO    2 I = 1, NSAMPS                                         
!                                                                               
            OUTPUT = B0*DATA_ARRAY(I) + B1*X1 + B2*X2                         
            OUTPUT = OUTPUT - ( A1*Y1 + A2*Y2 )                         
            Y2 = Y1                                                     
            Y1 = OUTPUT                                                 
            X2 = X1                                                     
            X1 = DATA_ARRAY(I)                                                
            DATA_ARRAY(I) = OUTPUT                                            
!                                                                               
    2     CONTINUE                                                      
!                                                                               
          JPTR = JPTR + 3                                               
!                                                                               
    1   CONTINUE                                                        
!                                                                               
        IF (   ZP ) THEN                                                
!                                                                               
          JPTR = 1                                                      
          DO    3 J = 1, NSECTS                                         
!                                                                               
            X1 = 0.0                                                    
            X2 = 0.0                                                    
            Y1 = 0.0                                                    
            Y2 = 0.0                                                    
            B0 = SN(JPTR)                                               
            B1 = SN(JPTR+1)                                             
            B2 = SN(JPTR+2)                                             
            A1 = SD(JPTR+1)                                             
            A2 = SD(JPTR+2)                                             
!                                                                               
            DO    4 I = NSAMPS, 1, -1                                   
!                                                                               
              OUTPUT = B0*DATA_ARRAY(I) + B1*X1 + B2*X2                       
              OUTPUT = OUTPUT - ( A1*Y1 + A2*Y2 )                       
              Y2 = Y1                                                   
              Y1 = OUTPUT                                               
              X2 = X1                                                   
              X1 = DATA_ARRAY(I)                                              
              DATA_ARRAY(I) = OUTPUT                                          
!                                                                               
    4       CONTINUE                                                    
!                                                                               
            JPTR = JPTR + 3                                             
!                                                                               
    3     CONTINUE                                                      
!                                                                               
        END IF                                                          
!                                                                               
      RETURN                                                            
      END                                                               
!--------------------------------------------------------------
!                                                                  DESIGN       
!                                                                               
!  Subroutine to design IIR digital filters from analog prototypes.             
!                                                                               
!  Input Arguments:                                                             
!  ----------------                                                             
!                                                                               
!    IORD                Filter order (10 MAXIMUM)                              
!                                                                               
!    TYPE                Character*2 variable containing filter type            
!                          LOWPASS (LP)                                         
!                          HIGHPASS (HP)                                        
!                          BANDPASS (BP)                                        
!                          BANDREJECT (BR)                                      
!                                                                               
!    APROTO              Character*2 variable designating analog prototype      
!                          Butterworth (BU)                                     
!                          Bessel (BE)                                          
!                          Chebyshev Type I (C1)                                
!                          Chebyshev Type II (C2)                               
!                                                                               
!    A                   Chebyshev stopband attenuation factor                  
!                                                                               
!    TRBNDW              Chebyshev transition bandwidth (fraction of            
!                          lowpass prototype passband width)                    
!                                                                               
!    FL                  Low-frequency cutoff                                   
!                                                                               
!    FH                  High-frequency cutoff                                  
!                                                                               
!    TS                  Sampling interval (in seconds)                         
!                                                                               
!  Output Arguments:                                                            
!  -----------------                                                            
!                                                                               
!    SN                  Array containing numerator coefficients of             
!                        second-order sections packed head-to-tail.             
!                                                                               
!    SD                  Array containing denominator coefficients              
!                        of second-order sections packed head-to-tail.          
!                                                                               
!    NSECTS              Number of second-order sections.                       
!                                                                               
!                                                                               
      SUBROUTINE DESIGN( IORD, FTYPE, APROTO, A, TRBNDW, FL, FH, TS, SN, SD, NSECTS )                   
!                                                                               
        IMPLICIT NONE
      
        INTEGER IORD, NSECTS             
        CHARACTER*2 FTYPE, APROTO                                       
        REAL*4 A, TRBNDW, FL, FH, TS, SN(1), SD(1)         
        

        CHARACTER*3 STYPE(10)
        COMPLEX P(10), Z(10)                                                
        REAL*4 DCVALUE,FLW,FHW,OMEGAR,EPS,RIPPLE,WARP

!                                                                               
!  Analog prototype selection                                                   
!                                                                               
        IF (     APROTO .EQ. 'BU' ) THEN                                
!                                                                               
          CALL BUROOTS( P, STYPE, DCVALUE, NSECTS, IORD )               
!                                                                               
        ELSE IF (    APROTO .EQ. 'BE' ) THEN                            
!                                                                               
          !CALL BEROOTS( P, STYPE, DCVALUE, NSECTS, IORD )               
!                                                                               
        ELSE IF (    APROTO .EQ. 'C1' ) THEN                            
!                                                                               
          !CALL CHEBPARM( A, TRBNDW, IORD, EPS, RIPPLE )                 
          !CALL C1ROOTS( P, STYPE, DCVALUE, NSECTS, IORD, EPS )          
!                                                                               
        ELSE IF (    APROTO .EQ. 'C2' ) THEN                            
!                                                                               
          OMEGAR = 1. + TRBNDW                                          
          !CALL C2ROOTS( P, Z, STYPE, DCVALUE, NSECTS, IORD, A, OMEGAR ) 
!                                                                               
        END IF                                                          
!                                                                               
!  Analog mapping selection                                                     
!                                                                               
        IF (     FTYPE .EQ. 'BP' ) THEN                                  
!                                                                               
          FLW = WARP( FL*TS/2., 2. )                                    
          FHW = WARP( FH*TS/2., 2. )                                    
          CALL LPTBP( P, Z, STYPE, DCVALUE, NSECTS, FLW, FHW, SN, SD )  
!                                                                               
        ELSE IF (   FTYPE .EQ. 'BR' ) THEN                               
!                                                                               
          FLW = WARP( FL*TS/2., 2. )                                    
          FHW = WARP( FH*TS/2., 2. )                                    
          !CALL LPTBR( P, Z, STYPE, DCVALUE, NSECTS, FLW, FHW, SN, SD )  
!                                                                               
        ELSE IF (    FTYPE .EQ. 'LP' ) THEN                              
!                                                                               
          FHW = WARP( FH*TS/2., 2. )                                    
          !CALL LP( P, Z, STYPE, DCVALUE, NSECTS, SN, SD )               
          !CALL CUTOFFS( SN, SD, NSECTS, FHW )                           
!                                                                               
        ELSE IF (    FTYPE .EQ. 'HP' ) THEN                              
!                                                                               
          FLW = WARP( FL*TS/2., 2. )                                    
          !CALL LPTHP( P, Z, STYPE, DCVALUE, NSECTS, SN, SD )            
          !CALL CUTOFFS( SN, SD, NSECTS, FLW )                           
!                                                                               
        END IF                                                          
!                                                                               
!  Bilinear analog to digital transformation                                    
!                                                                               
        CALL BILIN2( SN, SD, NSECTS )                                   
!                                                                               
      RETURN                                                            
      END                                                               
!-------------------------------------------------------------------
! BUROOTS -- SUBROUTINE TO COMPUTE BUTTERWORTH POLES FOR                        
!   NORMALIZED LOWPASS FILTER                                                   
!                                                                               
! LAST MODIFIED:  SEPTEMBER 7, 1990                                             
!                                                                               
!  OUTPUT ARGUMENTS:                                                            
!  -----------------                                                            
!      P              COMPLEX ARRAY CONTAINING POLES                            
!                       CONTAINS ONLY ONE FROM EACH                             
!                       COMPLEX CONJUGATE PAIR, AND                             
!                       ALL REAL POLES                                          
!                                                                               
!      RTYPE          CHARACTER ARRAY INDICATING 2ND ORDER SECTION              
!                       TYPE:                                                   
!                         (SP)  SINGLE REAL POLE                                
!                         (CP)  COMPLEX CONJUGATE POLE PAIR                     
!                         (CPZ) COMPLEX CONJUGATE POLE-ZERO PAIRS               
!                                                                               
!      DCVALUE        MAGNITUDE OF FILTER AT ZERO FREQUENCY                     
!                                                                               
!      NSECTS         NUMBER OF SECOND ORDER SECTIONS                           
!                                                                               
!  INPUT ARGUMENTS:                                                             
!  ----------------                                                             
!                                                                               
!      IORD           DESIRED FILTER ORDER                                      
!                                                                               
!                                                                               
      SUBROUTINE BUROOTS( P, RTYPE, DCVALUE, NSECTS, IORD )             
!                    
                                                           
        IMPLICIT NONE
        COMPLEX P(1)                                                    
        CHARACTER*3 RTYPE(1)                                            
        REAL*4 DCVALUE
        INTEGER NSECTS,IORD

        REAL*4 PI,ANGLE
        INTEGER HALF,K
        

!                                                                               
        PI=3.14159265                                                   
!                                                                               
        HALF = IORD/2                                                   
!                                                                               
! TEST FOR ODD ORDER, AND ADD POLE AT -1                                        
!                                                                               
        NSECTS = 0                                                      
        IF (    2*HALF .LT. IORD ) THEN                                 
          P(1) = CMPLX( -1., 0. )                                       
          RTYPE(1) = 'SP'                                               
          NSECTS = 1                                                    
        END IF                                                          
!                                                                               
        DO    1  K = 1, HALF                                            
          ANGLE = PI * ( .5 + FLOAT(2*K-1) / FLOAT(2*IORD) )            
          NSECTS = NSECTS + 1                                           
          P(NSECTS) = CMPLX( COS(ANGLE), SIN(ANGLE) )                   
          RTYPE(NSECTS) = 'CP'                                          
    1   CONTINUE                                                        
!                                                                               
        DCVALUE = 1.0                                                   
!                                                                               
      RETURN                                                            
      END                                                               
!-------------------------------------------------------------------
! WARP -- FUNCTION, APPLIES TANGENT FREQUENCY WARPING TO COMPENSATE             
!         FOR BILINEAR ANALOG -> DIGITAL TRANSFORMATION                         
!                                                                               
! ARGUMENTS:                                                                    
! ----------                                                                    
!                                                                               
!      F       ORIGINAL DESIGN FREQUENCY SPECIFICATION (HERTZ)                  
!      TS      SAMPLING INTERVAL (SECONDS)                                      
!                                                                               
!  LAST MODIFIED:  SEPTEMBER 20, 1990                                           
!                                                                               
      REAL*4  FUNCTION WARP( F , TS )                                      
!                                                                               
        IMPLICIT NONE

        REAL*4 F,TS
        REAL*4 TWOPI,ANGLE

        TWOPI = 6.2831853                                               
        ANGLE = TWOPI*F*TS/2.                                           
        WARP = 2.*TAN(ANGLE)/TS                                         
        WARP = WARP/TWOPI                                               
!                                                                               
      RETURN                                                            
      END                                                               
!------------------------------------------------------------------
!                                                    LPTBP                      
!                                                                               
!  Subroutine to convert an prototype lowpass filter to a bandpass filter via   
!    the analog polynomial transformation.  The lowpass filter is               
!    described in terms of its poles and zeros (as input to this routine).      
!    The output consists of the parameters for second order sections.           
!                                                                               
!  Input Arguments:                                                             
!  ----------------                                                             
!                                                                               
!    P                       Array containing poles                             
!                                                                               
!    Z                       Array containing zeros                             
!                                                                               
!    RTYPE                   Character array containing type information        
!                              (SP) single real pole  or                        
!                              (CP) complex conjugate pole pair  or             
!                              (CPZ) complex conjugate pole/zero pairs          
!                                                                               
!    DCVALUE                 Zero frequency value of filter                     
!                                                                               
!    NSECTS                  Number of second-order sections upon input         
!                                                                               
!    FL                      Low-frequency cutoff                               
!                                                                               
!    FH                      High-frequency cutoff                              
!                                                                               
!  Output Arguments:                                                            
!  -----------------                                                            
!                                                                               
!    SN                      Numerator polynomials for second order             
!                              sections.                                        
!                                                                               
!    SD                      Denominator polynomials for second order           
!                              sections.                                        
!                                                                               
!    NSECTS                  Number of second order sections upon output        
!                              This subroutine doubles the number of            
!                              sections.                                        
!                                                                               
!                                                                               
      SUBROUTINE LPTBP( P, Z, RTYPE, DCVALUE, NSECTS, FL, FH, SN, SD )  

        IMPLICIT NONE

        COMPLEX P(*), Z(*)
        CHARACTER*3 RTYPE(*)                                            
        REAL*4 SN(*), SD(*), DCVALUE,FL,FH 
        INTEGER NSECTS

        COMPLEX  CTEMP, P1, P2, Z1, Z2, S, H
        REAL*4 PI,TWOPI,A,B,SCALE
        INTEGER N,I,IPTR

!                                                                               
        PI = 3.14159265                                                 
        TWOPI = 2.*PI                                                   
        A = TWOPI*TWOPI*FL*FH                                           
        B = TWOPI*( FH - FL )                                           
!                                                                               
        N = NSECTS                                                      
        NSECTS = 0                                                      
        IPTR = 1                                                        
        DO    1 I = 1, N                                                
!                                                                               
          IF (    RTYPE(I) .EQ. 'CPZ' ) THEN                            
!                                                                               
            CTEMP = ( B*Z(I) )**2 - 4.*A                                
            CTEMP = CSQRT( CTEMP )                                      
            Z1 = 0.5*( B*Z(I) + CTEMP )                                 
            Z2 = 0.5*( B*Z(I) - CTEMP )                                 
            CTEMP = ( B*P(I) )**2 - 4.*A                                
            CTEMP = CSQRT( CTEMP )                                      
            P1 = 0.5*( B*P(I) + CTEMP )                                 
            P2 = 0.5*( B*P(I) - CTEMP )                                 
            SN( IPTR )     = REAL( Z1 * CONJG( Z1 ) )                   
            SN( IPTR + 1 ) = -2. * REAL( Z1 )                           
            SN( IPTR + 2 ) = 1.                                         
            SD( IPTR )     = REAL( P1 * CONJG( P1 ) )                   
            SD( IPTR + 1 ) = -2. * REAL( P1 )                           
            SD( IPTR + 2 ) = 1.                                         
            IPTR = IPTR + 3                                             
            SN( IPTR )     = REAL( Z2 * CONJG( Z2 ) )                   
            SN( IPTR + 1 ) = -2. * REAL( Z2 )                           
            SN( IPTR + 2 ) = 1.                                         
            SD( IPTR )     = REAL( P2 * CONJG( P2 ) )                   
            SD( IPTR + 1 ) = -2. * REAL( P2 )                           
            SD( IPTR + 2 ) = 1.                                         
            IPTR = IPTR + 3                                             
!                                                                               
            NSECTS = NSECTS + 2                                         
!                                                                               
          ELSE IF (   RTYPE(I) .EQ. 'CP' ) THEN                         
!                                                                               
            CTEMP = ( B*P(I) )**2 - 4.*A                                
            CTEMP = CSQRT( CTEMP )                                      
            P1 = 0.5*( B*P(I) + CTEMP )                                 
            P2 = 0.5*( B*P(I) - CTEMP )                                 
            SN( IPTR )     = 0.                                         
            SN( IPTR + 1 ) = B                                          
            SN( IPTR + 2 ) = 0.                                         
            SD( IPTR )     = REAL( P1 * CONJG( P1 ) )                   
            SD( IPTR + 1 ) = -2. * REAL( P1 )                           
            SD( IPTR + 2 ) = 1.                                         
            IPTR = IPTR + 3                                             
            SN( IPTR )     = 0.                                         
            SN( IPTR + 1 ) = B                                          
            SN( IPTR + 2 ) = 0.                                         
            SD( IPTR )     = REAL( P2 * CONJG( P2 ) )                   
            SD( IPTR + 1 ) = -2. * REAL( P2 )                           
            SD( IPTR + 2 ) = 1.                                         
            IPTR = IPTR + 3                                             
!                                                                               
            NSECTS = NSECTS + 2                                         
!                                                                               
          ELSE IF (  RTYPE(I) .EQ. 'SP' ) THEN                          
!                                                                               
            SN( IPTR )     = 0.                                         
            SN( IPTR + 1 ) = B                                          
            SN( IPTR + 2 ) = 0.                                         
            SD( IPTR )     = A                                          
            SD( IPTR + 1 ) = -B*REAL( P(I) )                            
            SD( IPTR + 2 ) = 1.                                         
            IPTR = IPTR + 3                                             
!                                                                               
            NSECTS = NSECTS + 1                                         
!                                                                               
          END IF                                                        
!                                                                               
    1   CONTINUE                                                        
!                                                                               
!  Scaling - use the fact that the bandpass filter amplitude at sqrt( omega_l * 
!            equals the amplitude of the lowpass prototype at d.c.              
!                                                                               
        S = CMPLX( 0., SQRT(A) )                                        
        H = CMPLX( 1., 0. )                                             
!                                                                               
        IPTR = 1                                                        
        DO    2 I = 1, NSECTS                                           
          H = H * ( ( SN(IPTR+2)*S + SN(IPTR+1) )*S + SN(IPTR) ) / ( ( SD(IPTR+2)*S + SD(IPTR+1) )*S + SD(IPTR) )            
          IPTR = IPTR + 3                                               
    2   CONTINUE                                                        
        SCALE = DCVALUE / SQRT( REAL( H ) * CONJG( H ) )                
                                                                        
        SN(1) = SN(1) * SCALE                                           
        SN(2) = SN(2) * SCALE                                           
        SN(3) = SN(3) * SCALE                                           
!                                                                               
      RETURN                                                            
    END
!------------------------------------------------------------
!                                BILIN2
!                                                                               
!  Transforms an analog filter to a digital filter via the bilinear transformati
!    Assumes both are stored as second order sections.  The transformation is   
!    done in-place.                                                             
!                                                                               
!  Input Arguments:                                                             
!  ----------------                                                             
!                                                                               
!    SN                   Array containing numerator polynomial coefficients for
!                           second order sections.  Packed head-to-tail.        
!                                                                               
!    SD                   Array containing denominator polynomial coefficients f
!                           second order sections.  Packed head-to-tail.        
!                                                                               
!    NSECTS               Number of second order sections.                      
!                                                                               
!                                                                               
      SUBROUTINE BILIN2( SN, SD, NSECTS )                               
! 
        IMPLICIT NONE
        REAL*4 SN(1), SD(1)
        INTEGER NSECTS

        INTEGER IPTR,I
        REAL*4 A0,A1,A2,SCALE

!                                                                               
        IPTR = 1                                                        
        DO    1 I = 1, NSECTS                                           
!                                                                               
          A0 = SD(IPTR)                                                 
          A1 = SD(IPTR+1)                                               
          A2 = SD(IPTR+2)                                               
!                                                                               
          SCALE = A2 + A1 + A0                                          
          SD(IPTR)   = 1.                                               
          SD(IPTR+1) = (2.*(A0 - A2)) / SCALE                           
          SD(IPTR+2) = (A2 - A1 + A0) / SCALE                           
!                                                                               
          A0 = SN(IPTR)                                                 
          A1 = SN(IPTR+1)                                               
          A2 = SN(IPTR+2)                                               
!                                                                               
          SN(IPTR)   = (A2 + A1 + A0) / SCALE                           
          SN(IPTR+1) = (2.*(A0 - A2)) / SCALE                           
          SN(IPTR+2) = (A2 - A1 + A0) / SCALE                           
!                                                                               
          IPTR = IPTR + 3                                               
!                                                                               
    1   CONTINUE                                                        
!                                                                               
      RETURN                                                            
      END                                                               
